use reqwest::{
    self,
    header::{ACCEPT, AUTHORIZATION, CONTENT_TYPE, USER_AGENT},
}; 
use serde::{Deserialize, Serialize}; 

#[derive(Serialize, Deserialize, Debug)]
struct Response {
    name: String,
    sha: String,
    size: u64,
    download_url: String, 
}

#[derive(Serialize, Deserialize, Debug)]
struct Content {
    content: Response,
}

#[tokio::main]
async fn main() -> Result<(), Box<dyn std::error::Error>> {
    let request_url = "https://api.github.com/repos/mjehrhart/sandbox/contents/my_image7.png";
    println!("{}", request_url);

    // data:image/png;base64,
    let base64_image = "";

    // Sha correct value is needed  
    let sha_value = "e04d656c9e3d879ebbd0bdeca23652cd9f767605";

    let mut json_body: Vec<u8>= format!(
        "{{\"message\": \"upload file\", \"commiter\": {{\"name\": \"{}\", \"email\":\"{}\"}}, \"sha\":\"{}\", \"content\": \"{}", 
        "matthew j.",
        "mjeoffline@gmail.com",
        sha_value,
        base64_image
    ).as_bytes().to_vec();
    json_body.extend_from_slice("\"}".as_bytes());

    let client = reqwest::Client::builder().build()?;
    let res = client
        .put(request_url)
        .header(
            AUTHORIZATION,
            "Bearer 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",
        )
        .header(USER_AGENT, "rustlang")
        .header(CONTENT_TYPE, "application/vnd.github.v3+json")
        .header(ACCEPT, "application/json")
        .body(json_body)
        .send()
        .await?;

    // let response: Response = res.json().await?;
    // println!("{:#?}", response);

    let response: Content = res.json().await?;
    println!("{:#?}", response );

    // let t = res.text().await?;
    // println!("{:#?}", t);

    Ok(())
}

